class ImprovedMaturityCalculator {
  calculateImprovedMaturity(rollout, processes, mindset, knowledge) {
    // Normalize values
    const normalized = {
      rolloutScope: [0, 0.25, 0.50, 0.75, 1.0][rollout],
      seRolesProcesses: [0, 0.20, 0.40, 0.60, 0.80, 1.0][processes],
      seMindset: [0, 0.25, 0.50, 0.75, 1.0][mindset],
      knowledgeBase: [0, 0.25, 0.50, 0.75, 1.0][knowledge]
    };

    // Calculate weighted score (0-100)
    const weightedScore = (
      normalized.rolloutScope * 0.20 +
      normalized.seRolesProcesses * 0.35 +
      normalized.seMindset * 0.25 +
      normalized.knowledgeBase * 0.20
    ) * 100;

    // Calculate balance penalty
    const values = Object.values(normalized);
    const mean = values.reduce((a,b) => a+b) / values.length;
    const variance = values.reduce((sum,val) => sum + Math.pow(val-mean,2), 0) / values.length;
    const stdDev = Math.sqrt(variance);
    const penalty = stdDev * 10;

    // Apply penalty
    let finalScore = weightedScore - penalty;

    // Check thresholds
    const minField = Math.min(...values);
    if (finalScore >= 60 && minField < 0.40) finalScore = Math.min(finalScore, 59.9);
    if (finalScore >= 40 && minField < 0.20) finalScore = Math.min(finalScore, 39.9);

    return {
      rawScore: weightedScore.toFixed(1),
      penalty: penalty.toFixed(1),
      finalScore: finalScore.toFixed(1),
      balanceScore: ((1 - stdDev) * 100).toFixed(1),
      fieldScores: {
        rolloutScope: (normalized.rolloutScope * 100).toFixed(1),
        seRolesProcesses: (normalized.seRolesProcesses * 100).toFixed(1),
        seMindset: (normalized.seMindset * 100).toFixed(1),
        knowledgeBase: (normalized.knowledgeBase * 100).toFixed(1)
      }
    };
  }
}
